+import { useEffect, useState } from 'react';
+import { TextField } from '../TextField';
+import { Movie } from '../../types/Movie';
+
+type Props = {
+  onAdd: (newMovie: Movie) => void;
+};
+
+export const NewMovie: React.FC<Props> = () => {
+  const [count, setCount] = useState(0);
+  const [title, setTitle] = useState('');
+  const [description, setDescription] = useState('');
+  const [imgUrl, setImageUrl] = useState('');
+  const [imdbUrl, setImdbUrl] = useState('');
+  const [imdbId, setImdbId] = useState('');
+
+  useEffect(() => {
+    setCount(prevCount => prevCount + 1);
+  }, []);
+
+  const titleChange = (event: string) => {
+    setTitle(event);
+  };
+
+  const descriptionChange = (event: string) => {
+    setDescription(event);
+  };
+
+  const imageUrlChange = (event: string) => {
+    setImageUrl(event);
+  };
+
+  const imdbUrlChange = (event: string) => {
+    setImdbUrl(event);
+  };
+
+  const imdbIdChange = (event: string) => {
+    setImdbId(event);
+  };
+
+  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
+    event.preventDefault();
+  };
+
+  const newMovie: Movie = {
+    title,
+    description,
+    imgUrl,
+    imdbUrl,
+    imdbId,
+  };
+
+  onAdd(newMovie);
+
+  const isValid =
+    title.trim() !== '' &&
+    description.trim() !== '' &&
+    imgUrl.trim() !== '' &&
+    imdbUrl.trim() !== '' &&
+    imdbId.trim() !== '';
+
+  return (
+    <form className="NewMovie" key={count} onSubmit={handleSubmit}>
+      <h2 className="title">Add a movie</h2>
+
+      <TextField
+        name="title"
+        label="Title"
+        value={title}
+        onChange={titleChange}
+        required
+      />
+
+      <TextField
+        name="description"
+        label="Description"
+        value={description}
+        onChange={descriptionChange}
+      />
+
+      <TextField
+        name="imgUrl"
+        label="Image URL"
+        value={imgUrl}
+        onChange={imageUrlChange}
+      />
+
+      <TextField
+        name="imdbUrl"
+        label="Imdb URL"
+        value={imdbUrl}
+        onChange={imdbUrlChange}
+      />
+
+      <TextField
+        name="imdbId"
+        label="Imdb ID"
+        value={imdbId}
+        onChange={imdbIdChange}
+      />
+
+      <div className="field is-grouped">
+        <div className="control">
+          <button
+            type="submit"
+            data-cy="submit-button"
+            className="button is-link"
+            disabled={!isValid}
+          >
+            Add
+          </button>
+        </div>
+      </div>
+    </form>
+  );
+};

