+import { useState } from 'react';
+import { TextField } from '../TextField';
+import { Movie } from '../../types/Movie';
+
+type Props = {
+  onAdd: (newMovie: Movie) => void;
+};
+
+export const NewMovie: React.FC<Props> = ({ onAdd }) => {
+  const [title, setTitle] = useState('');
+  const [description, setDescription] = useState('');
+  const [imgUrl, setImageUrl] = useState('');
+  const [imdbUrl, setImdbUrl] = useState('');
+  const [imdbId, setImdbId] = useState('');
+  const [imgUrlError, setImgUrlError] = useState('');
+  const [imgdbUrlError, setImgdbUrlError] = useState('');
+  const [imdbIdError, setImdbIdError] = useState('');
+
+  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
+    event.preventDefault();
+
+    const newMovie: Movie = {
+      title,
+      description,
+      imgUrl,
+      imdbUrl,
+      imdbId,
+    };
+
+    onAdd(newMovie);
+    setTitle('');
+    setDescription('');
+    setImageUrl('');
+    setImdbUrl('');
+    setImdbId('');
+  };
+
+  const blur = (event: React.FocusEvent<HTMLInputElement>) => {
+    const { name, value } = event.target;
+
+    switch (name) {
+      case 'imgUrl':
+        setImgUrlError(value.trim() === '' ? 'Error' : '');
+        break;
+      case 'imdbUrl':
+        setImgdbUrlError(value.trim() === '' ? 'Error' : '');
+        break;
+      case 'imdbId':
+        setImdbIdError(value.trim() === '' ? 'Error' : '');
+        break;
+      default:
+        break;
+    }
+  };
+
+  const isValid =
+    title.trim() !== '' &&
+    description.trim() !== '' &&
+    imgUrl.trim() !== '' &&
+    imdbUrl.trim() !== '' &&
+    imdbId.trim() !== '';
+
+  // const titleChange = (event: string) => {
+  //   setTitle(event);
+  // };
+
+  // const descriptionChange = (event: string) => {
+  //   setDescription(event);
+  // };
+
+  // const imageUrlChange = (event: string) => {
+  //   setImageUrl(event);
+  // };
+
+  // const imdbUrlChange = (event: string) => {
+  //   setImdbUrl(event);
+  // };
+
+  // const imdbIdChange = (event: string) => {
+  //   setImdbId(event);
+  // };
+
+  return (
+    <form className="NewMovie" onSubmit={handleSubmit}>
+      <h2 className="title">Add a movie</h2>
+
+      <TextField
+        name="title"
+        label="Title"
+        value={title}
+        onChange={event => setTitle(event.target.value)}
+        required
+      />
+
+      <TextField
+        name="description"
+        label="Description"
+        value={description}
+        onChange={event => setDescription(event.target.value)}
+      />
+
+      <TextField
+        name="imgUrl"
+        label="Image URL"
+        value={imgUrl}
+        onChange={event => setImageUrl(event.target.value)}
+        onBlur={blur}
+        error={imgUrlError}
+      />
+
+      <TextField
+        name="imdbUrl"
+        label="Imdb URL"
+        value={imdbUrl}
+        onChange={event => setImdbUrl(event.target.value)}
+        onBlur={blur}
+        error
+      />
+
+      <TextField
+        name="imdbId"
+        label="Imdb ID"
+        value={imdbId}
+        onChange={imdbIdChange}
+      />
+
+      <div className="field is-grouped">
+        <div className="control">
+          <button
+            type="submit"
+            data-cy="submit-button"
+            className="button is-link"
+            disabled={!isValid}
+          >
+            Add
+          </button>
+        </div>
+      </div>
+    </form>
+  );
+};

